mapper.insert:
insert into cultureStay_board
(
	boardnum
	, userid
	, title
	, contents

	, originalfile
	, savedfile

) values (
	cultureStay_boardnum_seq.nextval
	, #{userid}
	, #{title}
	, #{contents}

	, #{originalfile}
	, #{savedfile}

)

mapper.delete:
delete 
	cultureStay_board 
where 
	userid like #{userid}
	and boardnum = #{boardnum}

mapper.update:
update
	cultureStay_board
set
	title = #{title}
	, contents = #{contents}

	, originalfile = #{originalfile}
	, savedfile = #{savedfile}

where
	boardnum = #{boardnum}
	and userid = #{userid}
