mapper.insert:
    INSERT INTO cultureStay_member
    (
        userid
        , password
        , name
        , gender
        , birth
        , phone
        , address_postcode
        , address_address
        , address_detailAddress
        , address_extraAddress
        , email
        , introduce

			, ogProfileImage
			, svProfileImage

    )
    VALUES
    (
        #{userid}
        , #{password}
        , #{name}
        , #{gender}
        , #{birth}
        , #{phone}
        , #{address_postcode}
        , #{address_address}
        , #{address_detailAddress}
        , #{address_extraAddress}
        , #{email}
        , #{introduce}

			, #{ogProfileImage}
			, #{svProfileImage}

    )

mapper.update:
UPDATE cultureStay_member
SET
    password = #{password},

    phone = #{phone}
    , address_postcode = #{address_postcode}
    , address_address = #{address_address}
    , address_detailAddress = #{address_detailAddress}
    , address_extraAddress = #{address_extraAddress}
    , email = #{email}
    , introduce = #{introduce}

WHERE
    userid = #{userid}
