mapper.insert:
insert into Program
(
programNum
, userid
, title
, content
, address
,detailed_address
, price

, start_date
, end_date

) values (
programNum_seq.nextval
, #{userid}
, #{title}
, #{content}
, #{address}
,#{detailed_address}
, #{price}

, #{start_date}
, #{end_date}
)

insert into recentClick
	(

	clickNum
	,programNum
	, userid


	) values (
	clickNum_seq.nextval
	, #{programNum}
	, #{userid}

	)

mapper.select:
SELECT count(*)
FROM Program_like
WHERE programNum = #{programNum} AND userid = #{userid}

mapper.insert:
insert into Program_like
( p_like_num
,userid
,programNum
)
values (
p_like_num_seq.nextval
, #{userid}
, #{programNum}
)

mapper.delete:
delete
Program_like
where
programNum = #{programNum}
and userid = #{userid}

